#! /usr/bin/env python3
# -*- coding: utf-8 -*-

# 可以检查任意文件是否是位图文件，如果是，打印出图片大小和颜色数。

import base64, struct

def bmp_info(data):
    # unpack把bytes变成相应的数据类型
    a = struct.unpack('<ccIIIIIIHH', data[:30])
    print(a)
    if a[0]+a[1] == b'BM' or b'BA':
        return {
            'width': a[-4],
            'height': a[-3],
            'color': a[-1]
        }

# 测试
bmp_data = base64.b64decode('')
bi = bmp_info(bmp_data)

assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')

